package lab.dennis.primefactor;

import java.util.ArrayList;
import java.util.List;

public class PrimeFactor {
    public List<Integer> generate(int n) {
        List<Integer> primes = new ArrayList<Integer>();
        int base = 2;
        while (n > 1) {
            if (n % base == 0) {
                primes.add(base);
                n /= base;
            } else {
                base++;
            }
        }
        return primes;
    }
}
